import { useReducer } from 'react';
import DigitButton from './DigitButton';
import './styles.css'

export const ACTIONS = {
  ADD_DIGITS : 'add-digits',
  CHOOSE_OPERATION : 'choose-operation',
  CLEAR : 'clear',
  DELETE_DIGIT : 'delete-digit',
  EVALUATE : 'evaluate'
}

function reducer(state,{type,payload}){

    switch(type){
      case ACTIONS.ADD_DIGITS:
        return {
          ...state,
          currentOperand : `${currentOperand || ""}${payload.digit}`

    }
  }

}


function App() {
  const [{currentOperand , prevOperand, operation},dispatch]=useReducer(reducer,{})

  dispatch({type: ACTIONS.ADD_DIGITS,payload : { digit:1 }})
  return (
    <div className="calculator-grid">
      <div className="output">
        <div className="previous-operand">{prevOperand} {operation}</div>
        <div className="current-opernad">{currentOperand}</div>
      </div>
        <button className="span-two">AC</button>
        <DigitButton>DEL</DigitButton>
        <button>รท</button>
        <DigitButton>1</DigitButton>
        <DigitButton>2</DigitButton>
        <DigitButton>3</DigitButton>
        <button>*</button>
        <DigitButton>4</DigitButton>
        <DigitButton>5</DigitButton>
        <DigitButton>6</DigitButton>
        <button>+</button>
        <DigitButton>7</DigitButton>
        <DigitButton>8</DigitButton>
        <DigitButton>9</DigitButton>
        <button>-</button>
        <DigitButton>.</DigitButton>
        <DigitButton>0</DigitButton>
        <button>.</button>
        <button>0</button>
        <button className="span-two">=</button>
    </div>
  );
}

export default App;
